package LearnJavaBasics;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 6;
		if (num/2 == 0)
		{
			System.out.println("Number is Even Number");
		}
		else
		{
			System.out.println("Number is Odd Number");
		}
	}

}
